package results;

import model.Event;

/**
 * The result is generated by Event service that has a event
 */
public class EventResult {
    /**
     * a single event
     */
    private Event event;
    private String message;

    /**
     * construct a single event
     *
     * @param event a single event that's generated in service
     */
    public EventResult(Event event, String message) {
        this.event = event;
        this.message = message;
    }

    public Event getEvent() {
        return event;
    }

    public String getMessage() {
        return message;
    }

}
