package results;

/**
 * This is the result generated by register service
 */
public class RegisterResult {
    /**
     * AuthToken
     */
    private final String token;
    /**
     * user's unique name
     */
    private final String userName;
    /**
     * a person's unique ID
     */
    private final String personID;

    /**
     * construct the result after using RegisterService
     *
     * @param token    AuthToken
     * @param userName user's unique name
     * @param personID a person's unique ID
     */
    public RegisterResult(String token, String userName, String personID) {
        this.token = token;
        this.userName = userName;
        this.personID = personID;
    }

    public String getToken() {
        return token;
    }

    public String getUserName() {
        return userName;
    }

    public String getPersonID() {
        return personID;
    }
}
